function getJson(_obj) {
    var arr = [];
    for (var _k in _obj) {
        arr.push({
            "key": _k,
            "val": _obj[_k]
        })
    }
    var Arr = arr.sort(function(obj1, obj2) {
        var val1 = obj1.key;
        var val2 = obj2.key;
        var val3 = obj1.val;
        var val4 = obj2.val;
        if (val1 < val2) {
            return -1
        } else if (val1 > val2) {
            return 1
        } else if (val1 = val2) {
            if (val3 < val4) {
                return -1
            } else if (val3 > val4) {
                return 1
            } else {
                return 0
            }
        }
    });
    var str = "";
    for (var i = 0; i < Arr.length; i += 1) {
        str += Arr[i].key + "=" + Arr[i].val + "&"
    }
    str += "linjia";
    return hex_md5(str).toUpperCase()
}
function sAjax(_url, _type, _obj, _callback) {
    function obj(_obj) {
        _obj.sign = getJson(_obj);
        return _obj
    }
    $.ajax({
        url: "http://www.linjia-cvs.cn/linjia_1/" + _url,
        cache: true,
        type: _type,
        data: obj(_obj),
        dataType: "json",
        success: function(data) {
            if ($(".loading")) {
                setTimeout(function() {
                    $(".loading").remove()
                }, 120)
            }
            _callback(data)
        },
        error: function(err) {}
    })
}
function timeOut(_num, _min) {
    var _id;
    function postMoneyTime() {
        if (_num == 0) {
            _min -= 1;
            _num = 59
        }
        if (_num < 10) {
            if (_min < 10) {
                $(".postMoney_time").text("0" + _min + " : 0" + _num)
            } else {
                $(".postMoney_time").text(_min + " : 0" + _num)
            }
        } else {
            if (_min < 10) {
                $(".postMoney_time").text("0" + _min + " : " + _num)
            } else {
                $(".postMoney_time").text(_min + " : " + _num)
            }
        }
        _num -= 1;
        _id = setTimeout(postMoneyTime, 1000);
        if (_min == 0 && _num == 0) {
            clearTimeout(_id);
            a("超时未支付");
            window.location.href = "home.html"
        }
    }
    postMoneyTime()
}
